import styles from './GameDetail.module.css'
import { useEffect, useState } from "react"
import { useSelector } from "react-redux"
import { useDispatch } from "react-redux"
import { useParams } from "react-router-dom"
import { getGameById } from "../../Redux/Actions"
function GameDetail () {
    const gameDetail = useSelector(initialState => initialState.gameDetail)
    const dispatch = useDispatch()
    const platforms = gameDetail.platforms.slice('')
    const tags = gameDetail.tags.slice('#')
    const [active, setActive] = useState(false)
    const {id} = useParams()
    useEffect(() => {
        dispatch(getGameById(id))
    }, [])
    const dropdown = () => {
        if(!active){
            setActive(true)
        } else {
            setActive(false)
        }
    }
    console.log(tags)
    return(
        <div className={styles.all}>
            <div className={styles.container}>
            <p className={styles.gameTitle}>{gameDetail?.name}</p>
            <img className={styles.image} src={gameDetail.background_image}/>
            </div>
            <div className={styles.gameInfo}>
            <p>Release Date: {gameDetail.released}</p>
            <p>Rating: {gameDetail.rating}</p>
            <button onClick={dropdown} className={styles.dropdown}>Play it on: </button>
            {(active) ?   <ul className={styles.uList}>
                {platforms.map(each => {
                return(
                    <li className={styles.listItems}>{each}</li>
                )
             })}
            </ul> : <></>}
            <p>Devs: {gameDetail.developer}</p>
            <p>Metacritic score:{gameDetail.metacritic}</p>
            <p>Tags: {tags}</p>
            </div>
            <p>Buy it on: {gameDetail.stores}</p>
            <p>Description: {gameDetail.description}</p>
        </div>
    )
}


export default GameDetail
