import axios from 'axios'
import { useSelector, useDispatch} from 'react-redux';
import { useState, useEffect } from 'react'
import styles from './ShowGames.module.css'
import FilterBar from './FilterBar';
import Pagination from './Pagination'
import Game from './Game'
import { useNavigate } from 'react-router-dom';
import { sortAtoZ, sortZtoA, sortByRating } from '../../Redux/Actions';



function ShowGames(props) {
    const navigate = useNavigate()
    const games = useSelector(initialState => initialState.gamesInfo)
    const dispatch = useDispatch()
    const [active, setActive] = useState(false)
    const [loading, setLoading] = useState(true)
    const [currentPage, setCurrentPage] = useState(1)
    const [gamesPerPage, setGamesPerPage] = useState(9)
    const totalGames = games[0]?.length;
    const lastIndex = currentPage * gamesPerPage
    const firstIndex = lastIndex - gamesPerPage
    useEffect(()=> {
        if(totalGames >= 100)
        setLoading(false)
      
    },[games])
    
    const sortFromA = () => {
        dispatch(sortAtoZ())
        navigate('/home')
    }
    
    const sortFromZ = () => {
        dispatch(sortZtoA())
        navigate('/home')
    }   

    const sortByRate = () => {
        dispatch(sortByRating())
        navigate('/home')
    }

    const showFilters= () => {
        if(!active) setActive(true)
        else setActive(false)
    }

    if(loading){
        return(
            <div className={styles.loading}><p>Loading...</p></div>
        )
    }
    return(
    
        <div className={styles.gamesContainer}>
       <FilterBar></FilterBar>
       <div className={styles.sorts}>
        <button className={styles.button} onClck={() => showFilters}>Show Filters</button>
        <button className={styles.button} onClick={() => {sortFromA()}}>Sort A to Z</button>
        <button className={styles.button} onClick={() => {sortFromZ()}}>Sort Z to A</button>
        <button className={styles.button} onClick={() => {sortByRate()}}>Sort by rating</button>
       </div>
       { games[0].map(game => {
            return(
            <Game
                id={game.id}
                key={game.id}
                name = {game.name}
                rating = {game.rating}
                background_image = {game.background_image}
            />
            )    
        }).slice(firstIndex, lastIndex)}
          <div className={styles.pages}>
          <Pagination 
          gamesPerPage={gamesPerPage}
          currentPage={currentPage} 
          setCurrentPage={setCurrentPage}
          totalGames={totalGames}/>
          </div>
          <button onClick={()=>{
            navigate('/post')
          }}>Post YOUR GAME</button>
          </div>

    )
}


export default ShowGames
