import { useSelector, useDispatch } from "react-redux"
import styles from './FilterBar.module.css'
import { addFilter, removeFilter } from "../../Redux/Actions"
import { useState } from "react"
import { useNavigate } from "react-router-dom"
function FilterBar(){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const [active, setActive] = useState(false)
    const filterBy = (genre) => {
        
        if(active){
            dispatch(removeFilter(genre))
            setActive(false)}
        else {
            dispatch(addFilter(genre))
            navigate('/filterBy')
            setActive(true)
        }
    }
  
    const genres = useSelector(initialState => initialState.gamesGenres)
    const item = genres[0].map((genre) =>{
       return(<li className={styles.li} key={genre}>
            <button  onClick={() => filterBy({genre})} className={(active) ? styles.active : styles.unActive}>{genre}
            </button>
        </li>)
    }) 
    return(
        <nav className={styles.nav}>
            <ul className={styles.genresBar}>
                {item}
            </ul>
        </nav>
    )
}

export default FilterBar
