import { useSelector, useDispatch } from "react-redux"
import styles from './FilterBar.module.css'
import { addFilter, removeFilter, removeFilteredGames } from "../../Redux/Actions"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
function FilterBar(){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const activeFilter = useSelector(initialState => initialState.activeFilter)
    const filterBy = (genre) => {
        if(activeFilter.includes(genre.name)){
            dispatch(removeFilteredGames(genre.name))
            dispatch(removeFilter(genre.name))
            }
        else {
            dispatch(addFilter(genre.name))
            navigate('/filterBy')
        }
    }
  
    const genres = useSelector(initialState => initialState.gamesGenres)
    return(
        <nav className={styles.nav}>
            <ul className={styles.genresBar}>
            {genres[0].map((genre) =>{
            return (<li key={genre.name}>
                    <button className={(activeFilter.includes(genre.name)) ? styles.active : styles.unActive} onClick={() => filterBy(genre)}>{genre}
                    </button>
                </li>)
    }) }
            </ul>
        </nav>
    )
}

export default FilterBar
