import { useSelector, useDispatch } from "react-redux"
import styles from './FilterBar.module.css'
import { addFilter } from "../../Redux/Actions"
function FilterBar(){
    const filter = useSelector(initialState => initialState.activeFilter)
    const dispatch = useDispatch()
    const filterBy = (genre) => {
        dispatch(addFilter(genre))
    }


    const genres = useSelector(initialState => initialState.gamesGenres)
    const item = genres[0].map((genre) =>{
       return(<li className={styles.li} key={genre}>
            <a onClick={filterBy({genre})}>{genre}
            </a>
        </li>)
    }) 
    return(
        <nav className={styles.nav}>
            <ul className={styles.genresBar}>
                {item}
            </ul>
        </nav>
    )
}

export default FilterBar
