export const validateGameName = (form,error,setError) => {
    if(!form.name) {setError({...error, name: 'This field is obligatory'})}
    else {
        if(form.name.length >= 30) {setError({...error, name: 'Max Characters : 30'})}
        else {
            setError({...error, name: ''})
        }
    }
}


export const validateDescription =  (form,error,setError) => {
    if(!form.description) {setError({...error, description: 'This field is obligatory'})}
    else {
        if(form.description.length > 1000) setError({...error, description: 'Max characters: 1000'})
        else {
            setError({...error, description: ''})
        }
    }
}


export const validateRelease = (form, error, setError) => {
    if(!form.released) {
        setError({...error, released: 'This field is obligatory'})
    } else {
        setError({...error, released: ''})
    }

}


export const validateImage =(form, error, setError) => {
    const urlPattern = new RegExp('(?:https?):\/\/(\w+:?\w*)?(\S+)(:\d+)?(\/|\/([\w#!:.?+=&%!\-\/]))?');
    if(!form.image_one){
        setError({...error, image_one: 'This field is obligatory'})
    } else {
        if(urlPattern.test(form.image_one)) setError({...error, image_one: 'Has to be a Url'})
        else  setError({...error, image_one: ''})
    }
}
