import axios from "axios";

export const ADD_FAVORITE = 'ADD_FAVORITE';
export const REMOVE_FAVORITE = 'REMOVE_FAVORITE'
export const GET_INFO = 'GET_INFO'
export const GET_GENRES = 'GET_GENRES'
export const ADD_FILTER = 'ADD_FILTER'
export const REMOVE_FILTER = 'REMOVE_FILTER'
export const ADD_FILTERED_GAME= 'ADD_FILTERED_GAME'
export const REMOVE_FILTERED_GAME= 'REMOVE_FILTERED_GAME'
export const SORT_A_TO_Z='SORT_A_TO_Z'


export const getInfo =  () => {
   return dispatch => {
        axios.get('http://localhost:3001/videogames')
        .then(response =>
            {console.log(response.data)
                dispatch({
                type: 'GET_INFO',
                payload: response.data
            })}
        )
   }
   
}


export const addFilteredGames = (game) => {
    return{
        type: 'ADD_FILTERED_GAME',
        payload: game
    }
}

export const removeFilteredGames = (genre) => {
    return{
        type: 'REMOVE_FILTERED_GAME',
        payload: genre
    }
}

export const getGenres = () => {
    return dispatch => {
        axios.get('http://localhost:3001/genres')
        .then(response =>
            {   dispatch({
                type: 'GET_GENRES',
                payload: response.data
            })}
        )
   }
}

export const sortAtoZ = () => {
   return{
    type: 'SORT_A_TO_Z',
    payload: function sort(toOrder){
            toOrder = toOrder[0].sort((a,b) => {
            if(a.name < b.name)return 1
            if(a.name > b.name) return -1
            if(a.name == b.name) return 0
        })
        return toOrder
    }
   }
}

export const addFilter = (genre) => {
    return{
        type: 'ADD_FILTER',
        payload: genre
    }
}

export const removeFilter = (genre) => {
    return{
        type: 'REMOVE_FILTER',
        payload: genre
    }
}


export const addFavorite = (character) =>{
    return{
        type: 'ADD_FAVORITE',
        payload: character
    }
}

export const removeFavorite = (id) => {
    return{
        type: 'REMOVE_FAVORITE',
        payload: id
    }
}
