import { useSelector, useDispatch } from "react-redux"
import styles from './FilterBar.module.css'
import { addFilter, removeFilter } from "../../Redux/Actions"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
function FilterBar(){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const [active, setActive] = useState([])
    const filterBy = (genre) => {
        if(active){
            dispatch(removeFilter(genre))
            setActive(genre)}
        else {
            dispatch(addFilter(genre))
            navigate('/filterBy')
            
        }
    }

  
    const genres = useSelector(initialState => initialState.gamesGenres)
    const item = genres[0].map((genre) =>{
       return(<li key={genre} className={(active) ? styles.active : styles.unActive}>
            <button  onClick={() => filterBy({genre})}>{genre}
            </button>
        </li>)
    }) 
    return(
        <nav className={styles.nav}>
            <ul className={styles.genresBar}>
            {genres[0].map((genre) =>{
       return(<li key={genre}>
            <button className={(active.inlcudes(genre)) ? styles.active : styles.unActive} onClick={() => filterBy({genre})}>{genre}
            </button>
        </li>)
    }) }
            </ul>
        </nav>
    )
}

export default FilterBar
