import axios from 'axios'
import { useSelector, useDispatch} from 'react-redux';
import { useState, useEffect } from 'react'
import styles from './ShowGames.module.css'
import FilterBar from './FilterBar';
import Pagination from './Pagination'
import Game from './Game'
import { useNavigate } from 'react-router-dom';
import { sortAtoZ, sortZtoA, sortByRating } from '../../Redux/Actions';



function ShowGames(props) {
    const navigate = useNavigate()
    const games = useSelector(initialState => initialState.gamesInfo)
    const dispatch = useDispatch()
    const [loading, setLoading] = useState(true)
    const [currentPage, setCurrentPage] = useState(1)
    const [gamesPerPage, setGamesPerPage] = useState(9)
    const totalGames = games[0]?.length;
    const lastIndex = currentPage * gamesPerPage
    const firstIndex = lastIndex - gamesPerPage
    useEffect(()=> {
        if(totalGames == 100)
        setLoading(false)
      
    },[games])
    
    const sortFromA = () => {
        dispatch(sortAtoZ())
        navigate('/home')
    }
    
    const sortFromZ = () => {
        dispatch(sortZtoA())
        navigate('/home')
    }   

    const sortByRate = () => {
        dispatch(sortByRating())
        navigate('/home')
    }

    if(loading){
        return(
            <div className={styles.loading}><p>Loading...</p></div>
        )
    }

   
   
    

    return(
        <div>
        <div className={styles.gamesContainer}>
       <FilterBar></FilterBar>
       <button  onClick={() => {sortFromA()}}>Sort A to Z</button>
        <button onClick={() => {sortFromZ()}}>Sort Z to A</button>
        <button onClick={() => {sortByRate()}}>Sort by rating</button>
       { games[0].map(game => {
            return(
            <Game
                key={game.id}
                name = {game.name}
                rating = {game.rating}
                background_image = {game.background_image}
            />
            )    
        }).slice(firstIndex, lastIndex)}
        </div>
          <div className={styles.pages}>
          <Pagination 
          gamesPerPage={gamesPerPage}
          currentPage={currentPage} 
          setCurrentPage={setCurrentPage}
          totalGames={totalGames}/>
          </div>
          </div>
    )
}


export default ShowGames
