import styles from './Error.module.css'
import {useSelector, useDispatch} from 'react-redux'
import { getInfo, removeError} from '../../Redux/Actions'
function Error(props) {
    const dispatch = useDispatch()
    const error = useSelector(state => state.serverErrors)
    const goBack = () =>{
        dispatch(removeError())
        navigate('/home')
    }
    return(
        <div className={styles.container}> 
            <h3 className={styles.text}>Oops, something went wrong: {error.message} :(</h3>
            <h3 className={styles.text}>Server responded with status: {error.response.status} </h3>
            <h3 className={styles.text}>{error.response.data.error}</h3>
            <button onclick={goBack}>Game Over</button>
        </div>
    )
}

export default Error
