import { useEffect } from "react"
import { useSelector } from "react-redux"
import { useDispatch } from "react-redux"
import { useParams } from "react-router-dom"
import { getGameById } from "../../Redux/Actions"
function GameDetail () {
    const gameDetail = useSelector(initialState => initialState.gameDetail)
    const dispatch = useDispatch()
    const {id} = useParams()
    useEffect(() => {
        dispatch(getGameById(id))
    }, [])
    console.log(gameDetail)
    return(
        <div>
            <p>{gameDetail?.name}</p>
            <img src={gameDetail.background_image}/>
            <img src={gameDetail.background_image_additional}/>
            <p>Rating: {gameDetail.rating}</p>
            <p>Play it on: {gameDetail.platforms[0]}</p>
            <p>Description: {gameDetail.description}</p>
            <p>Devs: {gameDetail.developers[0].name}</p>
            <p>Metacritic score:{gameDetail.metacritic}</p>
            <p>Buy it on: {gameDetail.stores[0]}</p>
            <p>Tags: {gameDetail.tags[0]}</p>
        </div>
    )
}


export default GameDetail
