import { useSelector, useDispatch } from "react-redux"
import styles from './FilterBar.module.css'
import { addFilter, removeFilter } from "../../Redux/Actions"
import { useEffect, useState } from "react"
import { useNavigate } from "react-router-dom"
function FilterBar(){
    const dispatch = useDispatch()
    const navigate = useNavigate()
    const [activeFilters, setActiveFilters] = useState([])
    const filterBy = (genre) => {
        if(activeFilters.includes(genre)){
            dispatch(removeFilter(genre))
            setActiveFilters([])
            }
        else {
            dispatch(addFilter(genre))
            setActiveFilters(genre)
            navigate('/filterBy')
        }
    }

    console.log(activeFilters)
    const genres = useSelector(initialState => initialState.gamesGenres)
    const item = genres[0].map((genre) =>{
       return(<li key={genre.genre}>
            <button   className={(activeFilters.includes(genre)) ? styles.active : styles.unActive} onClick={() => filterBy({genre})}>{genre}
            </button>
        </li>)
    }) 
    return(
        <nav className={styles.nav}>
            <ul className={styles.genresBar}>
            {genres[0].map((genre) =>{
       return(<li key={genre}>
            <button className={(activeFilters.includes(genre)) ? styles.active : styles.unActive} onClick={() => filterBy(genre)}>{genre}
            </button>
        </li>)
    }) }
            </ul>
        </nav>
    )
}

export default FilterBar
