import axios from 'axios'
import { useState, useEffect } from 'react'
import styles from './ShowGames.module.css'
import Pagination from './Pagination'
import Game from './Game'
function ShowGames() {
    const [games, setGames] = useState([])
    const [loading, setLoading] = useState(true)
    const [currentPage, setCurrentPage] = useState(1)
    const [gamesPerPage, setGamesPerPage] = useState(5)
    useEffect( () => {axios.get('http://localhost:3001/videogames')
    .then(response => {
        setGames(oldGames => [...oldGames, response.data])
        setLoading(false)
    })},[])
    const totalGames = games.flat().length;

    if(loading){
        return(
            <div className={styles.loading}><p>Loading</p></div>
        )
    }

    return(
        <div>
        {games[0].map(game => {
            return(
            <Game
                name = {game.name}
                rating = {game.rating}
            />
            )    
        })}
        <Pagination 
        gamesPerPage={gamesPerPage}
        currentPage={currentPage} 
        setCurrentPage={setCurrentPage}
        totalGames={totalGames}/>
        </div>
    )
}


export default ShowGames
