import axios from 'axios'


export const validateGameName = (form,error,setError) => {
    if(!form.name) {setError({...error, name: 'This field is obligatory'})}
    else {
        if(form.name.length >= 30) {setError({...error, name: 'Max Characters : 30'})}
        else {
            setError({...error, name: ''})
        }
    }
}


export const validateDescription =  (form,error,setError) => {
    if(!form.description) {setError({...error, description: 'This field is obligatory'})}
    else {
        if(form.description.length > 1000) setError({...error, description: 'Max characters: 1000'})
        else {
            setError({...error, description: ''})
        }
    }
}


export const validateRelease = (form, error, setError) => {
    if(!form.released) {
        setError({...error, released: 'This field is obligatory'})
    } else {
        setError({...error, released: ''})
    }

}


export const validateImage =(form, error, setError) => {
    const urlPattern = new RegExp(/[-a-zA-Z0-9@:%._\+~#=]{1,256}\.[a-zA-Z0-9()]{1,6}\b([-a-zA-Z0-9()@:%_\+.~#?&//=]*)?/gi);
    if(!form.image_one){
        setError({...error, image_one: 'This field is obligatory'})
    } else {
        if(!urlPattern.test(form.image_one)) setError({...error, image_one: 'Has to be a Url'})
        else  setError({...error, image_one: ''})
    }
}

export const validateStores = (form, error, setError) => {
    if(!form.stores){ setError({...error, stores: 'Include at least one'})}
    else{
         setError({...error, stores: ''})
    }
}

export const validateDevs = (form, error, setError) => {
    if(!form.developer){setError({...error, developer: 'Someone made te game!'})}
    else{
         setError({...error, developer: ''})
    }
}

export const validateGenres = (form, error, setError) => {
    if(!form.genres){ setError({...error, genres: 'Include at least one'})}
    else{
         setError({...error, genres: ''})
    }
}


export const validateTags = (form, error, setError) => {
    if(!form.tags){ setError({...error, tags: 'Include at least one'})}
    else{
         setError({...error, tags: ''})
    }
}


export const sendPost = ( {name, description, genres, image_one, metacritic,
    developer, platforms, playtime, released, stores, tags}) => {
    axios.post('http://localhost:3001/videogames', {name, description, genres, image_one, metacritic,
    developer, platforms, playtime, released, stores, tags})
}
